import React from 'react';
import {useFormik} from 'formik';
import * as Yup from 'yup';
const NuevoPlatillo = () => {
    //Validacion u leer los datos del formulario
    const formik = useFormik({
        initialValues:{
            nombre:'',
            precio:'',
            categoria:'',
            imagen: '',
            descripcion: '',
        },
        validationSchema:Yup.object({
            nombre: Yup.string()
            .min(3,'Los Platillos deben tener al menos 3 caracteres')
            .required('El nombre del platillo es obligatorio'),
            precio: Yup.number()
            .min(1,'Debes agregar un numero')
            .required('El precio es obligatorio'),
            categoria: Yup.string()
            .min(10, 'La categoria es obligatoria'),
            descripcion: Yup.string()
            .min(10, 'La descripcion debe ser mas larga')
            .required('La descripcion es obligatoria'),

        }),
        onSubmit: datos =>{
            console.log(datos);
        }
    })



    return ( 
        <>
        <h1 className="text-3xl font-light mb-4">Agregar Platillo</h1>
        <div className="flex justify-center mt-10">
            <div className="w-full max-w-3xl">
                <form
                    onSubmit={formik.handleSubmit}
                
                >
                    <div className="mb-4">
                        <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="nombre">Nombre</label>
                        <input
                        
                            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline "
                            id="nombre"
                            type="text"
                            placeholder="Nombre Plantillo"
                            value={formik.values.nombre}
                            onChange={formik.handleChange}
                            onBlur={formik.handleBlur}
                        />
                        {formik.touched.nombre && formik.errors.nombre ? (
                            <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 ">
                                <p>{formik.errors.nombre}</p>
                            </div>

                        ): null}
                    </div>
                    <div className="mb-4">
                        <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="precio">Precio</label>
                        <input
                        
                            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline "
                            id="precio"
                            type="number"
                            placeholder="$20"
                            min="0"
                            value={formik.values.precio}
                            onChange={formik.handleChange}
                            onBlur={formik.handleBlur}
                        />
                    </div>
                    <div className="mb-4">
                        <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="nombre">Categoria</label>
                       <select 
                        className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline "
                        id="precio"
                        name="categoria"
                        value={formik.values.categoria}
                        onChange={formik.handleChange}
                        onBlur={formik.handleBlur}
                       >
                           <option value="">--Seleccione--</option>
                           <option value="desayuno">Desayuno</option>
                           <option value="Comida">Comida</option>
                           <option value="cena">Cena</option>
                           <option value="bebida">Bebidas</option>
                           <option value="postres">Postres</option>
                           <option value="ensalada">Ensalada</option>

                       </select>
                    </div>
                    <div className="mb-4">
                        <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="imagen">Imagen</label>
                        <input
                        
                            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline "
                            id="imagen"
                            type="file"
                            value={formik.values.imagen}
                            onChange={formik.handleChange}
                            onBlur={formik.handleBlur}
                            
                        />
                    </div>
                    <div className="mb-4">
                        <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="descripcion">Descripcion</label>
                        <textarea
                        
                            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline h-40"
                            id="descripcion"
                            value={formik.values.descripcion}
                            onChange={formik.handleChange}
                            onBlur={formik.handleBlur}
                            
                            placeholder="Descripcion"
                        ></textarea>
                    </div>
                    <input
                        type="submit"
                        className="bg-gray-800 hover:bg-gray-900 w-full mt-5 p-2 text-white uppercase font-bold"
                        value="Agregar Platillo"

                    />
                </form>

            </div>
        </div>
        </>
     );
}
 
export default NuevoPlatillo;
